mul(ypos,frame,frameheight);
txtadd(crop,'0|',get(ypos),'|',get(framewidth),'|',get(frameheight));
if(frameoff LT 1,
delayedcall(get(framespeed), if(loaded, hotspot_animate() ) );
);

